INSERT INTO `ci_news` (`id`, `date`, `title`, `descr`, `text`) VALUES
(1, 1290939895, 'Каталог дизайнеров', 'Для отображения в каталоге дизайнеров, нужно задать услуги в аккаунте, которые Вы предоставляете.', '<p>Для отображения в каталоге дизайнеров, нужно задать услуги в аккаунте, которые Вы предоставляете.</p>'),
(2, 1292788916, 'Предоставляемые услуги', 'Просим всех дизайнеров задать предоставляемые услуги, для отображение вашего профиля в каталоге вебдизайнеров. Для этого нужно перейти в аккаунт, в раздел услуги.', '<p>Просим всех дизайнеров задать предоставляемые услуги, для отображение вашего профиля в каталоге вебдизайнеров. Для этого нужно перейти в аккаунт, в раздел услуги.</p>'),
(4, 1293204852, 'Рейтинг вебдизайнов', 'На сервис Dlance.ru введён рейтинг дизайнов, для участия в нём необходимо загрузить дизайн сайта, затем подтвердить участие в рейтинге в разделе аккаунта – дизайны.', '<p>На сервис Dlance.ru введён рейтинг дизайнов, для участия в нём необходимо загрузить дизайн сайта, затем подтвердить участие в рейтинге в разделе аккаунта &ndash; дизайны.</p>'),
(5, 1294580397, 'События', 'На сервис добавлен раздел события, куда будут записываться различные события на сервисе - персональные сообщения, поступающие платежи.', '<p>На сервис добавлен раздел события, куда будут записываться различные события на сервисе - персональные сообщения, поступающие платежи.</p>');
